import React, { useState } from "react";

import InputText from "../components/input-text/input-text";
import WeShopButton from "../components/we-shop-button/we-shop-button";
import ConnectService from "../../services/connect-service";
import "./sign-in.scss";
function SignIn(props) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  function inputEmail() {
    return (
      <InputText
        placeholder={"Email"}
        type={"email"}
        id={"username"}
        value={email}
        setValue={e => setEmail(e)}
      ></InputText>
    );
  }
  function inputPassword() {
    return (
      <InputText
        placeholder={"Password"}
        textVissible={false}
        type={"password"}
        value={password}
        setValue={pass => setPassword(pass)}
      ></InputText>
    );
  }
  function inputConfirmPassword() {
    if (props.newUser) {
      return (
        <InputText
          placeholder={"Confirm Password"}
          textVissible={false}
          type={"password"}
          value={confirmPassword}
          setValue={pass => setConfirmPassword(pass)}
        ></InputText>
      );
    }
  }
  function onConfirm() {
    if (props.newUser) {
      ConnectService.register({
        email: email,
        password: password
      });
    } else {
      ConnectService.connect({
        email: email,
        password: password
      });
    }
    props.onClose();
  }
  function onCanncel() {
    props.onClose();
  }
  return (
    <div className="sign-in-container">
      <div className="input-container">
        <div className="input-fields-container">
          {inputEmail()} {inputPassword()} {inputConfirmPassword()}
        </div>
        <div className="buttons">
          <WeShopButton
            onClick={e => onConfirm()}
            text={newUser ? "Register" : "Connect"}
          ></WeShopButton>
          <WeShopButton
            seconderyButton={true}
            onClick={e => onCanncel()}
            text={"cancel"}
          ></WeShopButton>
        </div>
      </div>
      <div className="new-user-container">
        <WeShopButton
          onClick={e => setNewUSer(!props.newUser)}
          text={newUser ? "I already have an acount" : "register"}
        ></WeShopButton>
      </div>
    </div>
  );
}
export default SignIn;
