import { combineReducers, Action, AnyAction } from "redux";
export default combineReducers({});

export type WeShoopState = {
  userName: string;
  popUp: string;
};
const INITIALE_STATE: WeShoopState = {
  userName: "",
  popUp: "",
};

function userReducer(state: WeShoopState, action: AnyAction) {
  switch (action.type) {
    case "USER_NAME": {
      return {
        ...state,
        userName: action.payload,
      };
    }
    default: {
      return state;
    }
  }
}
function popUpReducer(state: WeShoopState, action: AnyAction) {
  switch (action.type) {
    case "OPEN_POPUP": {
      return {
        ...state,
        popUp: action.payload,
      };
    }
    case "CLOSE_POPUP": {
      return {
        ...state,
        popUp: "",
      };
    }
    default: {
      return state;
    }
  }
}
export const rootReducer = combineReducers({
  userReducer,
  popUpReducer,
});
