import React, { useState } from "react";

import InputText from "../components/input-text/input-text";
import WeShopButton from "../components/we-shop-button/we-shop-button";
import ConnectService from "../../services/connect-service";
import "./sign-in.scss";
function SignIn(props) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [newUser, setNewUSer] = useState("");
  let validMail = false;
  let validPassword = false;
  let validConfirmPassword = false;
  function checkValidation(type, value) {
    switch (type) {
      case "email": {
        validMail = true;
        break;
      }
      case "password": {
        validPassword = true;
        break;
      }
      case "confirmPassword": {
        validConfirmPassword = true;
        break;
      }
    }
  }
  function inputEmail() {
    return (
      <InputText
        valid={validMail}
        placeholder={"Email"}
        type={"email"}
        value={email}
        id={"username"}
        setValue={e => setEmail(e)}
        onfocusout={e => checkValidation("email", e)}
      ></InputText>
    );
  }
  function inputPassword() {
    return (
      <InputText
        valid={validPassword}
        placeholder={"Password"}
        textVissible={false}
        type={"password"}
        value={password}
        setValue={pass => setPassword(pass)}
        id="password"
        onfocusout={e => checkValidation("password", e)}
      ></InputText>
    );
  }
  function inputConfirmPassword() {
    if (newUser) {
      return (
        <InputText
          valid={validConfirmPassword}
          placeholder={"Confirm Password"}
          textVissible={false}
          type={"password"}
          value={confirmPassword}
          setValue={pass => setConfirmPassword(pass)}
          onfocusout={e => checkValidation("confirmPassword", e)}
        ></InputText>
      );
    }
  }
  function onConfirm() {
    if (newUser) {
      ConnectService.register({
        email: email,
        password: password
      });
    } else {
      ConnectService.connect({
        email: email,
        password: password
      });
    }
    props.onClose();
  }
  function onCanncel() {
    props.onClose();
  }
  return (
    <form>
      <div className="sign-in-container">
        <div className="input-container">
          <div className="input-fields-container">
            {inputEmail()} {inputPassword()} {inputConfirmPassword()}
          </div>
          <div className="buttons">
            <WeShopButton
              onClick={e => onConfirm()}
              text={newUser ? "Register" : "Connect"}
            ></WeShopButton>
            <WeShopButton
              seconderyButton={true}
              onClick={e => onCanncel()}
              text={"cancel"}
            ></WeShopButton>
          </div>
        </div>
        <div className="new-user-container">
          <WeShopButton
            onClick={e => setNewUSer(!newUser)}
            text={newUser ? "I already have an acount" : "register"}
          ></WeShopButton>
        </div>
      </div>
    </form>
  );
}
export default SignIn;
