  //       .matches(/(?=.*[0-9])/, "Password must contain a number.")
  //   }
  // ];
  function openPopup(tabName) {
    switch (tabName) {
      case "My Acount": {
        break;
      }
      case "Products": {
        break;
      }
      case "Today's Deals": {
        break;
      }
      case "Help": {
        break;
      }
      case "Register": {
        setShowPopup(false);
        setPopup(null);
        break;
      }
      case "Connect": {
        setPopup(
          <SignIn
            newUser={true}
            onClose={closePopup}
            onRegister={tab => openPopup(tab)}
            inputs={i}
          ></SignIn>
        );
        setShowPopup(true);
        break;
      }
      default: {
        console.log("Register");
        break;
      }
    }
  }
  const closePopup = () => {
    setShowPopup(false);
    setPopup(null);
  };

  return (
    <div className="we-shop">
      {showPopup ? <WeShopPopup content={popUp}></WeShopPopup> : null}
      <TopBar
        tabs={tabs}
        openPopUp={popupName => openPopup(popupName)}
      ></TopBar>
    </div>
