import React, { useState } from "react";

import InputText from "../components/input-text/input-text";
import WeShopButton from "../components/we-shop-button/we-shop-button";
import ConnectService from "../../services/connect-service";
import { Formik } from "formik";
import * as Yup from "yup";

import "./sign-in.scss";
const SignIn = () => (
  //const [email, setEmail] = useState("");
  // const [password, setPassword] = useState("");
  // const [confirmPassword, setConfirmPassword] = useState("");
  // const [newUser, setNewUSer] = useState("");
  // const [validMail, setValidMail] = useState(false);
  // const [validPassword, setValidPassword] = useState(false);
  // const [validConfirmPassword, setValidConfirmPassword] = useState(false);

  // function onConfirm() {
  //   if (newUser) {
  //     ConnectService.register({
  //       email: email,
  //       password: password
  //     });
  //   } else {
  //     ConnectService.connect({
  //       email: email,
  //       password: password
  //     });
  //   }
  //   props.onClose();
  // }
  // function onCanncel() {
  //   props.onClose();
  // }

  // return (
  <Formik
    initialValues={{
      email: "",
      password: "",
      confirmPassword: "",
      newUser: false
    }}
    onSubmit={(values, { setSubmitting }) => {
      setTimeout(() => {
        console.log("Logging in", values);
        setSubmitting(false);
      }, 500);
    }}
    validationSchema={Yup.object().shape({
      email: Yup.string()
        .email()
        .required("Required"),
      password: Yup.string()
        .required("No password provided.")
        .min(8, "Password is too short - should be 8 chars minimum.")
        .matches(/(?=.*[0-9])/, "Password must contain a number."),
      confirmPassword: Yup.string()
        .required("No password provided.")
        .min(8, "Password is too short - should be 8 chars minimum.")
        .matches(/(?=.*[0-9])/, "Password must contain a number.")
    })}
  >
    {props => {
      const {
        values,
        touched,
        errors,
        isSubmitting,
        handleChange,
        handleBlur,
        handleSubmit
      } = props;
      function inputEmail() {
        return (
          <InputText
            className={errors.password && touched.password && "error"}
            name={"email"}
            // valid={validMail}
            placeholder={"Email"}
            type={"email"}
            value={values.email}
            id={"username"}
            setValue={handleChange}
            handleBlur={handleBlur}
          ></InputText>
        );
      }
      function inputPassword() {
        return (
          <InputText
            className={errors.password && touched.password && "error"}
            name={"password"}
            // valid={validPassword}
            placeholder={"Password"}
            textVissible={false}
            type={"password"}
            value={values.password}
            setValue={handleChange}
            id="password"
            handleBlur={handleBlur}
          ></InputText>
        );
      }
      function inputConfirmPassword() {
        if (values.newUser) {
          return (
            <InputText
              className={errors.password && touched.password && "error"}
              name={"confirmPassword"}
              // valid={validConfirmPassword}
              placeholder={"Confirm Password"}
              textVissible={false}
              type={"password"}
              value={values.confirmPassword}
              setValue={handleChange}
              onfocusout={handleBlur}
            ></InputText>
          );
        }
      }
      return (
        <form onSubmit={handleSubmit}>
          <div className="sign-in-container">
            <div className="input-container">
              <div className="input-fields-container">
                {inputEmail()} {inputPassword()} {inputConfirmPassword()}
              </div>
              <div className="buttons">
                <WeShopButton
                  type={"submit"}
                  disable={isSubmitting}
                  // onClick={onConfirm()}
                  text={values.newUser ? "Register" : "Connect"}
                ></WeShopButton>
                <WeShopButton
                  seconderyButton={true}
                  // onClick={e => onCanncel()}
                  text={"cancel"}
                ></WeShopButton>
              </div>
            </div>
            <div className="new-user-container">
              <WeShopButton
                className={errors.password && touched.password && "error"}
                onClick={e => (values.newUser = !values.newUser)}
                text={values.newUser ? "I already have an acount" : "register"}
              ></WeShopButton>
            </div>
          </div>
        </form>
      );
    }}
  </Formik>
);
//}
export default SignIn;
